import {Component} from "react";
import {Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import {BudgetView} from "../model/BudgetView";
import {TypeOperation} from "../enum/TypeOperation";
import {CategoryTypeOperation} from "../enum/CategoryTypeOperation";
import * as React from "react";

export interface Props{
	isShown: boolean;
	operation: BudgetView;
	hide: () => void;
	getAllOperation: () => void;
}

type State = {
	createdDate: Date;
	typeOperation: TypeOperation;
	categoryTypeOperation: CategoryTypeOperation;
	operationSum: number;
	description: string;
}

export class BudgetModal extends Component<Props, State>{
	constructor(props: any) {
		super(props);
		
		this.state = {
			createdDate: new Date(),
			typeOperation: TypeOperation.income,
			categoryTypeOperation: CategoryTypeOperation.car,
			operationSum: 0,
			description: ""
		}
	}
	render() {
		return (
			<div>
				<Modal size="lg"
					   aria-labelledby="contained-modal-title-vcenter"
					   isOpen={this.props.isShown}
					   fade={false}
				>
					<ModalHeader>
						<h1>{this.props.operation.id != 0 ? "Редактирование операции: " + this.props.operation.createdDate.toString() : "Добавление операции"}</h1>
					</ModalHeader>
					<ModalBody>
						
					</ModalBody>
					<ModalFooter>
						<button className={this.props.operation.id !=0 ? "btn btn-success" : "btn btn-primary"}>{this.props.operation.id !=0 ? "Сохранить" : "Добавить"}</button>
						<button onClick={() => this.props.hide()} className="btn btn-danger">Закрыть</button>
					</ModalFooter>
				</Modal>
			</div>
		);
	}
}
